'use strict';
const resourceMethod = require('./ResourceMethod');

module.exports = {
  create: resourceMethod({
    method: 'POST',
  }),

  update: resourceMethod({
    method: 'PUT',
    path: '/{id}',
  }),

  retrieve: resourceMethod({
    method: 'GET',
    path: '/{id}',
  }),

  del: resourceMethod({
    method: 'DELETE',
    path: '/{id}',
  }),
};
