'use strict';

const Resources = require('../../net/Resources');
const resourceMethod = Resources.method;

module.exports = Resources.extend({
  path: '/v1/orders',
  includeBasic: ['create', 'retrieve'],

  pay: resourceMethod({
    method: 'POST',
    path: '/{id}/pay',
  }),

  returnOrder: resourceMethod({
    method: 'POST',
    path: '/{id}/returns',
  }),
});
