'use strict';
const Resources = require('../../net/Resources');
const resourceMethod = Resources.method;

module.exports = Resources.extend({
  path: '/v1/tokens',
  includeBasic: ['retrieve'],
  create(id, callback) {
    return resourceMethod({
      host: function() {
        return this._cloverObj._config[this._cloverObj._env].token_url;
      },
      method: 'POST',
      headers: {
        'apikey': id.apiKey,
      },
    }).apply(this, [id]);
  },
});
